[asy]
unitsize(0.3 cm);

pair A, B, C, D, E, F, H;

A = (5,12);
B = (0,0);
C = (14,0);
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;
H = extension(B,E,C,F);

