draw((0,0)--(6,0)--(6,4)--(0,4)--cycle);
draw((0,0)--(6,2)--(0,4));
draw((0,0)--(0,4),dashed);
draw((0,0)--(6,0),dashed);
draw((6,0)--(6,4),dashed);
